initialData.css:
.scoreboard {
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Arial', sans-serif;
  color: white;
  background-color: rgba(29, 38, 51, 0.8);
  border-radius: 10px;
  padding: 10px 20px;
  font-size: 24px;
}
.team {
  display: flex;
  align-items: center;
}
.name {
  font-weight: bold;
  margin: 0 15px;
}
.score {
  background-color: rgba(0, 0, 0, 0.2);
  padding: 5px 15px;
  border-radius: 5px;
  font-weight: bold;
}
.separator {
  margin: 0 15px;
  font-weight: bold;
}
.logo {
  width: 40px;
  height: 40px;
  object-fit: contain;
}
